import React, { Component } from "react";
import axios from 'axios';
import ResultsList from "./components/ResultsList/ResultsList";
import Input from "./components/Input/Input";
import Button from "./components/Button/Button";
import { MainContainer, SearchAndButtonContainer } from "./AppStyles.js";
import Demo from './demo';


export default class App extends Component {
  state = {
    value: '',
    results: [],
    filteredResults: [],
    loaded: false,
  }

  componentDidMount() {
    axios.get(`http://localhost:8010/proxy/suburbs.json?q=Syd`)
      .then(res => {
        const results = res.data;
        this.setState({ results, loaded: true });
      })
  }

  onSelect = (result) => {
    this.setState({ value: result })
  }

  filterResults = () => {
    const inputValue = this.state.value.trim().toLowerCase();
    const inputLength = inputValue.length;
    let save = [];
    this.state.results.filter(suggestion => {
      console.log(suggestion)
      const item = suggestion.name.slice(0, inputLength).toLowerCase() === inputValue;
      if (item) {
        save.push(suggestion.name)
        console.log(save)
      }
    });
  }

  handleChange = (event) => {
    this.filterResults()
    this.setState({ value: event });
  }

  render() {
    return this.state.loaded ? (
      <section>
        <MainContainer>
          <SearchAndButtonContainer>
          <Input value={this.state.value} onChange={this.handleChange}/>
          <Button/>
          </SearchAndButtonContainer>
          { this.state.results &&
            <ResultsList
              onSelect={this.onSelect}
              loaded
              items={this.state.results}
            />

          }
        </MainContainer>
      </section>
    ): 'Loading'
  }
}
